function morty_ey(x,y){
	$.ajax({
		url: "../inc/action/map.php",
		type: "POST",
		data: {
			x: x, y: y
		},
		success: function (data) {
			image = JSON.parse(data);
			bootbox.dialog({
				message: '<img src="data:image/jpeg;base64,'+image.image+'" style="width:100%; height:470px"></img>',
				title: "Map",
				className: "map_",
				buttons: {
					primary: {
						label: "Close",
						className: "btn-primary no-border"
					}
				}
			}).find('.modal-body').css({'padding': '0'}); 
		}
	})
}